import './App.css';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import { db } from './firebase.config';
import { useState, useEffect } from "react"
import {collection, onSnapshot,where,query} from "firebase/firestore"

function App() {
  const todoRef = collection(db,"todos")
  const q = query(todoRef, where("timecategory", "==", "long-term"));
  const r = query(todoRef, where("timecategory", "==", "short-term"));
  const s = query(todoRef, where("timecategory", "==", "date"));

  const [todosLT, setTodosLT] = useState([])
  const [todosST, setTodosST] = useState([])

  const date = new Date();
  const day = date.getDay();
  const month = date.getMonth()+1;
  const year = date.getFullYear();

  const days =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"]

  const todayDays = days.slice(day-1).concat(days.slice(0,day-1))

  var tomorrow = new Date();
  tomorrow.setDate(tomorrow.getDate()+1);
  var tomorrow2 = new Date();
  tomorrow2.setDate(tomorrow.getDate()+2);
  var tomorrow3 = new Date();
  tomorrow3.setDate(tomorrow.getDate()+3);
  var tomorrow4 = new Date();
  tomorrow4.setDate(tomorrow.getDate()+4);
  var tomorrow5 = new Date();
  tomorrow5.setDate(tomorrow.getDate()+5);
  var tomorrow6 = new Date();
  tomorrow6.setDate(tomorrow.getDate()+6);


  const sToday = query(s, where("timecategory", "==", day.toLocaleString('fr-FR')));
  const sTomorrow = query(s, where("timecategory", "==", tomorrow.toLocaleString('fr-FR')));
  const sTomorrow2 = query(s, where("timecategory", "==", tomorrow2.toLocaleString('fr-FR')));
  const sTomorrow3 = query(s, where("timecategory", "==", tomorrow3.toLocaleString('fr-FR')));
  const sTomorrow4 = query(s, where("timecategory", "==", tomorrow4.toLocaleString('fr-FR')));
  const sTomorrow5 = query(s, where("timecategory", "==", tomorrow5.toLocaleString('fr-FR')));
  const sTomorrow6 = query(s, where("timecategory", "==", tomorrow6.toLocaleString('fr-FR')));



  useEffect(()=>{
    onSnapshot(q, snapshot => {
      setTodosLT(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
    onSnapshot(r, snapshot => {
      setTodosST(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
  }
)

  return (
    <div className="App">
      <div>{day}</div> <div>{month}</div> <div>{year}</div>

      <table>
      <thead><tr><th style={{color:"#720004"}}>{todayDays[0]}</th><th>{todayDays[1]}</th><th>{todayDays[2]}</th><th>{todayDays[3]}</th><th>{todayDays[4]}</th><th>{todayDays[5]}</th><th>{todayDays[6]}</th></tr></thead>
      <tbody>
    <tr>
    <td>
      {day}
    </td>
    <td>
      {tomorrow.toLocaleDateString('fr-FR')}
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    </tr>
    </tbody>
    </table>

    <table>
      <thead><tr><th>Short-term</th><th>Long-term</th></tr></thead>
      <tbody>
    <tr>
    <td><div className="short-term">
      <table><thead><tr><th>Task</th><th>Deadline</th></tr></thead>
      <tbody>{todosST.map(todo => <tr key={todo.id}><td><div>&#x25B7; {todo.text}</div></td> <td><div>{todo.deadline}</div></td></tr>)}</tbody></table>

    </div>
    </td>
    <td><div className="long-term">
      {todosLT.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </div>
    </td>

    </tr>
    </tbody>
    </table>
    </div>
  );
}

export default App;
