import React, { Component } from 'react';
import { collection } from "firebase/firestore";
import { db } from './firebase.config';

class Todo extends Component {

    constructor(props) {
      super(props)

      this.state = {
          id:this.props.key,
          timeCategory:this.props.timeCategory,
          date:this.props.date,
          text:this.props.text,
          isSelected:false, 
        }

      this.selfRef = React.createRef()


      this.supress = this.supress.bind(this)

  }



  supress = async (e) => {
    const todoRef = collection(db,"todos")
    if ((window.confirm('Are you sure you wish to delete this article/book?'))) {
        todoRef.doc(this.state.id).delete();
    }
    }


    render() {

        if (this.state.timeCategory==="long-term") {
            return <tr key={this.state.id}> <td style={{backgroundColor:(this.state.isSelected ? "#FFAF19": "white")}} onKeyPress={this.supress()} onClick={()=> this.setState({isSelected:!this.state.isSelected})} className="todo">{this.state.text}</td></tr>
        } else if (this.state.timeCategory==="short-term") {
           return  <tr key={this.state.id} className="short-term-task todo" style={{backgroundColor:(this.state.isSelected ? "#FFAF19": "white")}} onClick={()=> this.setState({isSelected:!this.state.isSelected})}><td><div> {this.state.text}</div></td><td><div style={{textAlign:"center"}}>{this.state.date}</div></td></tr>
        } else if (this.state.timeCategory==="date") {
            return <div className="todo" style={{backgroundColor:(this.state.isSelected ? "#FFAF19": "white")}} key={this.state.id} onClick={()=> this.setState({isSelected:!this.state.isSelected})}>&#x25B7; {this.state.text}</div>
        }
    }
  }

  export default Todo;
