import './App.css';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import { db } from './firebase.config';
import { useState, useEffect } from "react"
import {collection, onSnapshot,where,query} from "firebase/firestore"

function App() {
  const todoRef = collection(db,"todos")
  const q = query(todoRef, where("timecategory", "==", "long-term"));
  const r = query(todoRef, where("timecategory", "==", "short-term"));
  const s = query(todoRef, where("timecategory", "==", "date"));

  const [todosLT, setTodosLT] = useState([])
  const [todosST, setTodosST] = useState([])

  const [todosD1, setTodosD1] = useState([])
  const [todosD2, setTodosD2] = useState([])
  const [todosD3, setTodosD3] = useState([])
  const [todosD4, setTodosD4] = useState([])
  const [todosD5, setTodosD5] = useState([])
  const [todosD6, setTodosD6] = useState([])
  const [todosD7, setTodosD7] = useState([])

  const days =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday","Sunday"]


  const date = new Date();
  const day = date.getDay();

  const nextDays = days.slice(day-1).concat(days.slice(0,day-1))

  const nextDates =[]

  for (let i=0; i< 7;i++) {
    var next = new Date();
    next.setDate(next.getDate()+i);
    nextDates.push(next);
  }

  const queriesDate=[]

  for (let i=0; i< 7;i++) {
    queriesDate.push(query(s, where("date", "==", nextDates[i].toLocaleString('fr-FR').split(' ')[0])))
  }

  const queriesDateRepeat=[]

  var dt= new Date("15/12/2022")
  console.log(dt.getDay())



  useEffect(()=>{
    onSnapshot(q, snapshot => {
      setTodosLT(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
    onSnapshot(r, snapshot => {
      setTodosST(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[0], snapshot => {
      setTodosD1(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[1], snapshot => {
      setTodosD2(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[2], snapshot => {
      setTodosD3(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[3], snapshot => {
      setTodosD4(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[4], snapshot => {
      setTodosD5(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[5], snapshot => {
      setTodosD6(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    onSnapshot(queriesDate[6], snapshot => {
      setTodosD7(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
  }
)

  return (
    <div className="App">

      <table>
      <thead><tr><th style={{color:"#720004"}}>{nextDays[0]}</th><th>{nextDays[1]}</th><th>{nextDays[2]}</th><th>{nextDays[3]}</th><th>{nextDays[4]}</th><th>{nextDays[5]}</th><th>{nextDays[6]}</th></tr></thead>
      <tbody>
    <tr>
    <td>
    {todosD1.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    <td>
    {todosD2.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    <td>
    {todosD3.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    <td>
    {todosD4.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    <td>
    {todosD5.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    <td>
    {todosD6.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    <td>
    {todosD7.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </td>
    </tr>
    </tbody>
    </table>

    <table>
      <thead><tr><th>Short-term</th><th>Long-term</th></tr></thead>
      <tbody>
    <tr>
    <td><div className="short-term">
      <table><thead><tr><th>Task</th><th>Deadline</th></tr></thead>
      <tbody>{todosST.map(todo => <tr key={todo.id}><td><div>&#x25B7; {todo.text}</div></td> <td><div>{todo.deadline}</div></td></tr>)}</tbody></table>

    </div>
    </td>
    <td><div className="long-term">
      {todosLT.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </div>
    </td>

    </tr>
    </tbody>
    </table>
    </div>
  );
}

export default App;
