import './App.css';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import { db } from './firebase.config';
import { useState, useEffect } from "react"
import {collection, onSnapshot,where,query,orderBy} from "firebase/firestore"

function App() {
  const todoRef = collection(db,"todos")
  const q = query(todoRef, where("timecategory", "==", "long-term"));
  const r = query(todoRef, where("timecategory", "==", "short-term"));

  const [todosLT, setTodosLT] = useState([])
  const [todosST, setTodosST] = useState([])

  const date = new Date();
  const day = date.getDay();
  const month = date.getMonth()+1;
  const year = date.getFullYear();

  const days =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"]

  const todayDays = days.slice(day-1).concat(days.slice(0,day-1))

  console.log(todayDays)

  useEffect(()=>{
    onSnapshot(q, snapshot => {
      setTodosLT(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
    onSnapshot(r, snapshot => {
      setTodosST(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
  }
)

  return (
    <div className="App">
      <div>{day}</div> <div>{month}</div> <div>{year}</div>

      <table>
      <thead><tr><th style={{color:"#720004"}}>Thursday</th><th>Friday</th><th>Saturday</th><th>Sunday</th><th>Monday</th><th>Tuesday</th><th>Wednesday</th></tr></thead>
      <tbody>
    <tr>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    <td>
    </td>
    </tr>
    </tbody>
    </table>

    <table>
      <thead><tr><th>Short-term</th><th>Long-term</th></tr></thead>
      <tbody>
    <tr>
    <td><div className="short-term">
      <table><thead><tr><th>Task</th><th>Deadline</th></tr></thead>
      <tbody>{todosST.map(todo => <tr key={todo.id}><td><div>&#x25B7; {todo.text}</div></td> <td><div>{todo.deadline}</div></td></tr>)}</tbody></table>

    </div>
    </td>
    <td><div className="long-term">
      {todosLT.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </div>
    </td>

    </tr>
    </tbody>
    </table>
    </div>
  );
}

export default App;
