import './App.css';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import { db } from './firebase.config';
import { useState, useEffect } from "react"
import {collection, onSnapshot,where,query} from "firebase/firestore"

function App() {
  const todoRef = collection(db,"todos")
  const q = query(todoRef, where("timecategory", "==", "long-term"));
  const r = query(todoRef, where("timecategory", "==", "short-term"));
  const [todosLT, setTodosLT] = useState([])
  const [todosST, setTodosST] = useState([])

  useEffect(()=>{
    onSnapshot(q, snapshot => {
      setTodosLT(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
    onSnapshot(r, snapshot => {
      setTodosST(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
  }
)

  return (
    <table>
      <thead><td>Long-term</td><td>Short-term</td></thead>
      <tbody>
    <tr><td><div className="App">
      {todosLT.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </div>
    </td>
    <td><div className="App">
      {todosST.map(todo => <p key={todo.id}>&#x25B7; {todo.text}</p>)}
    </div>
    </td>
    </tr>
    </tbody>
    </table>
  );
}

export default App;
