import './App.css';
import 'firebase/compat/firestore';
import { db } from './firebase.config';
import { useState, useEffect } from "react"
import {collection, onSnapshot,where,query,setDoc,doc} from "firebase/firestore"
import { v4 } from 'uuid';

function App() {
  const todoRef = collection(db,"todos")
  const queryLongTerm = query(todoRef, where("timecategory", "==", "long-term"));
  const queryShortTerm = query(todoRef, where("timecategory", "==", "short-term"));
  const queryDate = query(todoRef, where("timecategory", "==", "date"));

  const [todosLT, setTodosLT] = useState([])
  const [todosST, setTodosST] = useState([])

  const [todosD,setTodosD]=useState([])

  const [todosD1, setTodosD1] = useState([])
  const [todosD2, setTodosD2] = useState([])
  const [todosD3, setTodosD3] = useState([])
  const [todosD4, setTodosD4] = useState([])
  const [todosD5, setTodosD5] = useState([])
  const [todosD6, setTodosD6] = useState([])
  const [todosD7, setTodosD7] = useState([])

  const [todosDR1, setTodosDR1] = useState([])
  const [todosDR2, setTodosDR2] = useState([])
  const [todosDR3, setTodosDR3] = useState([])
  const [todosDR4, setTodosDR4] = useState([])
  const [todosDR5, setTodosDR5] = useState([])
  const [todosDR6, setTodosDR6] = useState([])
  const [todosDR7, setTodosDR7] = useState([])

  const days =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday","Sunday"]
  const date = new Date();
  const day = date.getDay();

  const nextDays = days.slice(day-1).concat(days.slice(0,day-1))

  const nextDates =[]
  const queriesDate=[]
  const queriesDateRepeat=[]

  const [formText,setFormText] = useState("")
  const [formTimeCategory,setFormTimeCategory]=useState("")
  const [formDate,setFormDate]=useState("")
  const [formRepeat,setFormRepeat] =useState("")

  useEffect(()=>{

    let j=7;

    for (let i=0; i< j;i++) {
      var next = new Date();
      next.setDate(next.getDate()+i);
      nextDates.push(next);
    }


    for (let i=0; i< j;i++) {
      queriesDate.push(query(query(queryDate, where("repeat", "==", "no")), where("date", "==", nextDates[i].toLocaleString('fr-FR').split(' ')[0])))
    }


    for (let i=0; i< j;i++) {
      queriesDateRepeat.push(query(query(queryDate, where("repeat", "==", "yes")),where("day","==",nextDates[i].getDay())))
    }


    onSnapshot(queryLongTerm, snapshot => {
      setTodosLT(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )
    onSnapshot(queryShortTerm, snapshot => {
      setTodosST(snapshot.docs.map(doc => {
        return {
          id:doc.id,
          ...doc.data()
        }
      }))
    }
    )

    for (let i=0;i<j;i++) {
      onSnapshot(queriesDate[i], snapshot => {
        let todoDCopy = todosD
        todoDCopy.push(snapshot.docs.map(doc => {
          return {
            id:doc.id,
            ...doc.data()
          }
        }))
        setTodosD(todoDCopy)
        console.log(todoDCopy)
      }
      )

    } 

  }
, [])

const addTodo = async (e) => {
    e.preventDefault();

    var x = v4();

    if (formTimeCategory=="long-term"){

      await setDoc(doc(db, "todos", x),{text:formText,timecategory:formTimeCategory})

    } else if (formTimeCategory=="short-term"){

      await setDoc(doc(db, "todos", x),{text:formText,timecategory:formTimeCategory,deadline:formDate})

    } else if (formTimeCategory=="date"){
      if (formRepeat=="yes"){
        const formatedDate = new Date(formDate);
        const formDay = formatedDate.getDay();

        await setDoc(doc(db, "todos", x),{text:formText,timecategory:formTimeCategory,date:formDate,repeat:formRepeat,day:formDay})
      } else {
        await setDoc(doc(db, "todos", x),{text:formText,timecategory:formTimeCategory,date:formDate,repeat:formRepeat})
      }
    }

    setFormText('');
    setFormDate('');
    setFormTimeCategory('');
    setFormRepeat('');
}

let urlAddImg = new URL("add.png",import.meta.url);

  return (
    <div className="App">


    <form onSubmit={addTodo}>
        <label>Task:</label> &nbsp; <input onChange={(e) => setFormText(e.target.value)} />
        &nbsp; &nbsp; <label>Time category:</label> &nbsp; <input onChange={(e) => setFormTimeCategory(e.target.value)} />
        <br/>
        &nbsp; &nbsp; <label>Deadline/date (YYYY-MM-DD):</label> &nbsp; <input onChange={(e) => setFormDate(e.target.value)} />
        &nbsp; &nbsp; <label>Repeat:</label> &nbsp; <input onChange={(e) => setFormRepeat(e.target.value)} />

        <button type="submit"><img className="form-button" src={urlAddImg}/></button>
      </form>


    <br/>

      <h2>Next week:</h2>

      <table className="next-week">
      <thead><tr><th style={{color:"#720004"}}>{nextDays[0]}</th><th>{nextDays[1]}</th><th>{nextDays[2]}</th><th>{nextDays[3]}</th><th>{nextDays[4]}</th><th>{nextDays[5]}</th><th>{nextDays[6]}</th></tr></thead>
      <tbody>
    <tr>
      {todosD.map(todoList => <td className="next-week-day"> {todoList.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}</td>)}
    </tr></tbody></table>

    <br/><br/>

    <h2>Next week:</h2>

      <table className="next-week">
      <thead><tr><th style={{color:"#720004"}}>{nextDays[0]}</th><th>{nextDays[1]}</th><th>{nextDays[2]}</th><th>{nextDays[3]}</th><th>{nextDays[4]}</th><th>{nextDays[5]}</th><th>{nextDays[6]}</th></tr></thead>
      <tbody>
    <tr><td className="today">
    {todosD1.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR1.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td><td className="next-week-day">
    {todosD2.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR2.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td><td className="next-week-day">
    {todosD3.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR3.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td><td className="next-week-day">
    {todosD4.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR4.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td><td className="next-week-day">
    {todosD5.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR5.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td><td className="next-week-day">
    {todosD6.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR6.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td><td className="next-week-day">
    {todosD7.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    {todosDR7.map(todo => <div className="todo-date" key={todo.id}>&#x25B7; {todo.text}</div>)}
    </td></tr></tbody></table>

    <br/><br/>


    <h2>Short-term:</h2>

    <table className="short-term"><thead><tr><th></th><th>Deadline</th></tr></thead>
      <tbody>{todosST.map(todo => <tr key={todo.id} className="short-term-task"><td><div> {todo.text}</div></td><td><div style={{textAlign:"center"}}>{todo.deadline}</div></td></tr>)}</tbody></table>

     <br/><br/>

     <h2>Long-term:</h2>

    <table className="long-term">
       <tbody>
      {todosLT.map(todo => <tr key={todo.id}> <td>{todo.text}</td></tr>)}
    </tbody>
    </table>
    </div>
  );
}

export default App;
